def draw_RF_comparison_bar_graph(reference, predicted, freqencyNAbove):
    # Extract all keys from predicted (since it has more keys)
    # keys = list(predicted.keys())
    keys = [k for k in reference if reference[k] > freqencyNAbove]
    # print(keys)
    # keys = [-1,0,1,2,3]

    # Build corresponding values from reference and predicted
    ref_values = [reference.get(k, 0) for k in keys]
    pred_values = [predicted[k] for k in keys]
    # print(ref_values)
    # print(pred_values)
    # Plotting
    x = range(len(keys))
    bar_width = 0.4

    plt.bar([i - bar_width/2 for i in x], ref_values, width=bar_width, label='PARDA Calculated')
    plt.bar([i + bar_width/2 for i in x], pred_values, width=bar_width, label='Our Prediction')

    plt.xlabel('Reuse Distances')
    plt.ylabel('Frequency')
    # plt.title('Reuse Profile Estimation and PARDA comparison')
    plt.xticks(x, keys)
    plt.legend()
    plt.tight_layout()
    plt.savefig(f"sardh_3_Out_1_rf_comp_rd_above_{freqencyNAbove}.png")
    plt.show()
